using Colorful;

using System;
using System.Drawing;

namespace FructoseLib.CLI.Visual.Static
{
    public enum FontType { BigMoney }

    public static partial class FontTypeExtensions
    {
        public static string GetEncodedFont(this FontType Value) => Value switch
        {
            FontType.BigMoney => "",
            _ => throw new NotSupportedException(),
        };
    }


    static partial class ConsoleComponents
    {
        public static void RenderAbout(string Version, string Name, int NameStringOffset = 1, int VersionStringOffset = 8, FontType FontType = FontType.BigMoney)
        {
            Colorful.Console.Title = $"{Name} v.{Version} by https://t.me/FructoseExp";
            Figlet Figlet = new(FigletFont.Load(Convert.FromBase64String(FontType.GetEncodedFont())));

            Colorful.Console.WriteWithGradient(Figlet.ToAscii($"{" ".PadLeft(NameStringOffset)}{Name}").ToString(), System.Drawing.Color.BlueViolet, System.Drawing.Color.Coral, 8);
            Colorful.Console.WriteWithGradient(Figlet.ToAscii($"{" ".PadLeft(VersionStringOffset)}*** v.{Version} ***").ToString(), System.Drawing.Color.BlueViolet, System.Drawing.Color.Coral, 8);
            Colorful.Console.ReplaceAllColorsWithDefaults();
            Colorful.Console.WriteLine();
            
        }

        public static void RenderAbout(string Version, string Name, System.Drawing.Color FirstGradientColor, System.Drawing.Color SecondGradientColor, int NameStringOffset = 1, int VersionStringOffset = 8, FontType FontType = FontType.BigMoney)
        {
            Colorful.Console.Title = $"{Name} v.{Version} by https://t.me/FructoseExp";
            Figlet Figlet = new(FigletFont.Load(Convert.FromBase64String(FontType.GetEncodedFont())));

            Colorful.Console.WriteWithGradient(Figlet.ToAscii($"{" ".PadLeft(NameStringOffset)}{Name}").ToString(), FirstGradientColor, SecondGradientColor, 8);
            Colorful.Console.WriteWithGradient(Figlet.ToAscii($"{" ".PadLeft(VersionStringOffset)}*** v.{Version} ***").ToString(), FirstGradientColor, SecondGradientColor, 8);
            Colorful.Console.ReplaceAllColorsWithDefaults();
            Colorful.Console.WriteLine();

        }
    }
}
