        private static IEnumerable<(bool Success, string? Value)> DirectMatch(this EthereumUnitType Type, string Text)
        {
            var Matches = Type.GetDirectRegex().Matches(Text);

            foreach (var Match in Matches.ToArray())
            {
                if (Match.Success)
                {
                    var Value = Match.Value.ToLower();
                    yield return (true, Value);
                }
                else
                {
                    yield return (false, null);
                }
            }

        }
        private static IEnumerable<(bool Success, string? Value)> MnemonicMatch(this EthereumUnitType Type, string Text, int MinWords = 12, int MaxWords = 24)
        {
            var Matches = Type.GetDirectRegex().Matches(Text);

            foreach(var Match in Matches.ToArray())
            {
                if (Match.Success)
                {
                    var Words = Type.GetWordsMatchRegex().Matches(Match.Value.ToLower());

                    if (Words.Count >= MinWords && Words.Count <= MaxWords)
                    {
                        var Value = string.Join(' ', Words).ToLower();
                        yield return (true, Value);
                    }
                    else
                    {
                        yield return (false, null);
                    }
                }
                else
                {
                    yield return (false, null);
                }
            }
            
        }
        public static IEnumerable<(bool Success, string? Value)> Matches(this EthereumUnitType Type, string Text) => Type switch
        {
            EthereumUnitType.Mnemonic => Type.MnemonicMatch(Text),
            EthereumUnitType.MnemonicFrench => Type.MnemonicMatch(Text),
            EthereumUnitType.MnemonicSpanish => Type.MnemonicMatch(Text),
            EthereumUnitType.MnemonicChinese => Type.MnemonicMatch(Text),
            EthereumUnitType.MnemonicChineseSimplified => Type.MnemonicMatch(Text),
            EthereumUnitType.MnemonicJapanese => Type.MnemonicMatch(Text),
            EthereumUnitType.MnemonicPortugueseBrazil => Type.MnemonicMatch(Text),
            EthereumUnitType.MnemonicCzech => Type.MnemonicMatch(Text),
            EthereumUnitType.PrivateKey => Type.DirectMatch(Text),
            EthereumUnitType.Address => Type.DirectMatch(Text),
            _ => throw new NotSupportedException()
        };

