        public static Regex GetDirectRegex(this EthereumUnitType Type) => Type switch
        {
            EthereumUnitType.Mnemonic => MnemonicDirectRegex.Value,
            EthereumUnitType.MnemonicFrench => MnemonicFrenchDirectRegex.Value,
            EthereumUnitType.MnemonicSpanish => MnemonicSpanishDirectRegex.Value,
            EthereumUnitType.MnemonicChinese => MnemonicChineseDirectRegex.Value,
            EthereumUnitType.MnemonicChineseSimplified => MnemonicChineseSimplifiedDirectRegex.Value,
            EthereumUnitType.MnemonicJapanese => MnemobicJapaneseDirectRegex.Value,
            EthereumUnitType.MnemonicPortugueseBrazil => MnemonicPortugueseBrazilDirectRegex.Value,
            EthereumUnitType.MnemonicCzech => MnemonicCzechDirectRegex.Value,
            EthereumUnitType.PrivateKey => PrivateKeyDirectRegex.Value,
            EthereumUnitType.Address => AddressDirectRegex.Value,
            _ => throw new NotSupportedException(),
        };
        public static Regex GetWordsMatchRegex(this EthereumUnitType Type) => Type switch
        {
