        public static IEthereumUnit Create(this EthereumUnitType Type, string Value, IEnumerable<(DerivationType Type, string Path, int Depth, int Offset)> DerivationOptions, (string Hash, long Line) Metadata) => Type switch
        {
            EthereumUnitType.Mnemonic => new EthereumMnemonic(Value, EthereumUnitType.Mnemonic, DerivationOptions, Metadata),
            EthereumUnitType.MnemonicFrench => new EthereumMnemonic(Value, EthereumUnitType.MnemonicFrench, DerivationOptions, Metadata),
            EthereumUnitType.MnemonicSpanish => new EthereumMnemonic(Value, EthereumUnitType.MnemonicSpanish, DerivationOptions, Metadata),
            EthereumUnitType.MnemonicChinese => new EthereumMnemonic(Value, EthereumUnitType.MnemonicChinese, DerivationOptions, Metadata),
            EthereumUnitType.MnemonicChineseSimplified => new EthereumMnemonic(Value, EthereumUnitType.MnemonicChineseSimplified, DerivationOptions, Metadata),
            EthereumUnitType.MnemonicJapanese => new EthereumMnemonic(Value, EthereumUnitType.MnemonicJapanese, DerivationOptions, Metadata),
            EthereumUnitType.MnemonicPortugueseBrazil => new EthereumMnemonic(Value, EthereumUnitType.MnemonicPortugueseBrazil, DerivationOptions, Metadata),
            EthereumUnitType.MnemonicCzech => new EthereumMnemonic(Value, EthereumUnitType.MnemonicCzech, DerivationOptions, Metadata),
            EthereumUnitType.PrivateKey => new EthereumPrivateKey(Value, Metadata),
            EthereumUnitType.Address => new EthereumAddress(Value, Metadata),
            _ => throw new NotSupportedException()
        };


        public static string[] ReadAp(this EthereumUnitType Type)
        {
            if (File.Exists(Type.GetAp()))
            {
                return File.ReadAllLines(Type.GetAp());
            }
            else
            {
                return Array.Empty<string>();
            }
        }

        public static void WriteToAp(this EthereumUnitType Type, string[] Units)
        {
            using var Stream = new FileStream(Type.GetAp(), FileMode.Append, FileAccess.Write, FileShare.ReadWrite);
            using var StreamWriter = new StreamWriter(Stream);
            foreach (var Unit in Units)
            {
                StreamWriter.WriteLine(Unit);
            }
        }
        public static object AntipublicLocker = new();
        public static void WriteToAp(this EthereumUnitType Type, string Unit)
        {
            lock (AntipublicLocker)
            {
                File.AppendAllText(Type.GetAp(), Unit + "\n");
            }
        }

        public static bool IsValid(this EthereumUnitType Type, string Value) => Type switch
        {
            EthereumUnitType.Mnemonic => IsMnemonicValid(Value, Wordlist.English),
            EthereumUnitType.MnemonicFrench => IsMnemonicValid(Value, Wordlist.French),
            EthereumUnitType.MnemonicSpanish => IsMnemonicValid(Value, Wordlist.Spanish),
            EthereumUnitType.MnemonicChinese => IsMnemonicValid(Value, Wordlist.ChineseTraditional),
            EthereumUnitType.MnemonicChineseSimplified => IsMnemonicValid(Value, Wordlist.ChineseSimplified),
            EthereumUnitType.MnemonicJapanese => IsMnemonicValid(Value, Wordlist.Japanese),
            EthereumUnitType.MnemonicPortugueseBrazil => IsMnemonicValid(Value, Wordlist.PortugueseBrazil),
            EthereumUnitType.MnemonicCzech => IsMnemonicValid(Value, Wordlist.Czech),
            EthereumUnitType.PrivateKey => IsPrivateKeyValid(Value),
            EthereumUnitType.Address => true,
            _ => throw new NotSupportedException()
        };
        private static bool IsMnemonicValid(string Value, Wordlist Wordlist)
        {
            try 
            {
                if (new NBitcoin.Mnemonic(Value, Wordlist).IsValidChecksum)
                {
                    return true;
                } else
                {
                    return false;
                }
            }
            catch
            {
                return false;
            }
        }
        private static bool IsPrivateKeyValid(string Value)
        {
            try
            {
                _ = Nethereum.Signer.EthECKey.GetPublicAddress(Value);
            }
            catch
            {
                return false;
            }

            return true;
        }
    }
